import React, { Component } from "react";

import logo from "../assets/logo.svg";
import UserProfileForm from "./UserProfileForm";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import { withAuth0 } from '@auth0/auth0-react';
const axios = require('axios');
let rowAccountsRecords = [];
let accountRecords = null;
class AccountView extends Component {

    constructor(props) {
        super(props);
        console.log(props);
        // const { user, isAuthenticated } = this.props.auth0;
        this.state = { email: props.email
    }
        this.getAccounts = this.getAccounts.bind(this);
        // this.state.accountNumber = this.props.accountNumber;
        this.state.rowsTransactions = [];
        // this.search = this.search.bind(this);
    }

    componentDidMount() {
        rowAccountsRecords = []
        let currentComponent = this;
        this.getAccounts(currentComponent);
    }

    getAccounts(currentComponent) {
        window.axios.get('/users/' + currentComponent.state.email + '/accounts').then(function (response) {
            console.log(response)
            // if (response && response.data && response.data.accounts) {
            //     currentComponent.setState({ accounts: response.data.accounts });
            //     accountRecords = response.data.accounts;
            //     response.data.accounts.forEach(tr => {
            //         rowAccountsRecords.push(<tr>
            //             <td>{tr.transaction_id}</td>
            //             <td>{tr.transaction_type}</td>
            //             <td>{tr.transaction_frequency}</td>
            //             <td>{tr.amount}</td>
            //             <td>{tr.to_account}</td>
            //             <td>{tr.transaction_status}</td>
            //             <td>{tr.transaction_date}</td>
            //         </tr>)
            //     })
            //     searchTransaction = rowTransactionsRecords;
            //     currentComponent.setState({ rowAccounts: rowAccountsRecords })
            // } else {
            //     currentComponent.setState({ accounts: null });
            // }
        }).catch(function (error) {
            // handle error
            console.log(error);
            accountRecords = []
            rowAccountsRecords = []
        })
    }

    render() {
        return (
            <div>
                if { accountRecords ? 
                <table class="table">
                    <thead>
                        <tr>
                            <th><abbr title="index">Transaction Id</abbr></th>
                            <th><abbr title="type"></abbr>Type</th>
                            <th><abbr title="frequency">Frequency</abbr></th>
                            <th><abbr title="amount">Amount</abbr></th>
                            <th><abbr title="fm_acc">To Account</abbr></th>
                            <th><abbr title="status">Status</abbr></th>
                            <th><abbr title="date">Date</abbr></th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.rowsTransactions}
                    </tbody>
                </table>
                :
                <div>
                    No Accounts Found
                    </div>
            }
            </div>
        )
    };
}

export default withAuth0(AccountView);
