import React, { Component } from "react";

import { Row, Col } from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const axios = require('axios');

let user = false;
class UserProfileForm extends Component {
    constructor(props) {
        super(props);
        this.state = {user: {}, email: props.email}
        this.createUser = this.createUser.bind(this);
        this.getUser = this.getUser.bind(this);
        this.updateUser = this.updateUser.bind(this);
      }
        componentDidMount() {
            let currentComponent = this;
            this.getUser(currentComponent);
        }

        getUser(currentComponent){
            axios.get('http://localhost:8080/users/'+currentComponent.state.email).then(function (response) {
                // handle success
                currentComponent.setState({user: response.data});
              })
              .catch(function (error) {
                // handle error
                console.log(error);
              })
            }

        createUser() {
            axios.post('http://localhost:8080/users', {
                "firstName": "Riddhi3",
                "lastName": "Jain",
                "email": "riddhi.jain+2@sjsu.edu",
                "identity_no": "abcdefghi+1",
                "dob": null,
                "address": null,
                "state": "california",
                "zipCode": null,
                "mobileNumber": null
                }).then(function (response) {
                this.user = response.data;
              })
              .catch(function (error) {
                console.log(error);
              });
        }

        updateUser(){
            console.log(this.state.user);
            // axios.post('http://localhost:8080/users', {
            //     "firstName": ,
            //     "lastName": "Jain",
