            //     this.user = response.data;
            //   })
            //   .catch(function (error) {
            //     console.log(error);
            //   });
        }
  render() {
    return (
        <div class="container">
        <div class="columns">
        <div class="column">
            <label class="label">First Name: {this.state.user.firstName}</label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" value={this.state.user.firstName} disabled/>
                {/* <input class="input" type="text" placeholder="Text input" value={this.state.user.firstName} /> */}
            </div>
        </div>
        <div class="column">
            <label class="label">Last Name: </label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" value={this.state.user.lastName} disabled/>
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Email Id:</label>
            <div class="control has-icons-left has-icons-right">
                <input class="input is-danger" type="email" placeholder="Email input"  value={this.state.user.email} disabled/>
                <span class="icon is-small is-left">
                    <i class="fas fa-envelope"></i>
                </span>
                <span class="icon is-small is-right">
                    <i class="fas fa-exclamation-triangle"></i>
                </span>
            </div>
            <p class="help is-danger">This email is invalid</p>
        </div>

        <div class="column">
            <label class="label">Mobile No: </label>
            <div class="control">
                <input class="input" type="tel" placeholder="Number input" value ={this.state.user.mobileNumber} disabled/>
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Identity No: </label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" value={this.state.user.identity_no} disabled/>
            </div>
        </div>

        <div class="column">
            <label class="label">Date Of Birth: </label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" value={this.state.user.dob} disabled/>
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Address: </label>
            <textarea class="textarea" placeholder="2 lines of textarea" rows="2" value={this.state.user.address} disabled></textarea>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">State: </label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" value={this.state.user.state} disabled/>
            </div>
        </div>

        <div class="column">
            <label class="label">Zip Code: </label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" value={this.state.user.zipCode} disabled/>
            </div>
        </div>
        </div>
        <div class="center">
        <div class="control">
            <button class="button is-primary" onClick={this.updateUser}>Update</button>
        </div>
        </div>
        </div>


        // ------------------

        // <div class="container">
        // <div class="columns">
        // <div class="column">
        //     <label class="label">First Name: </label>
        //     <div>{this.state.user.firstName}</div>
