import React, { useState } from "react";
import { NavLink as RouterNavLink } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import {
  Collapse,
  Container,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  Button,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";

import { useAuth0 } from "@auth0/auth0-react";

const NavBar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const {
    user,
    isAuthenticated,
    loginWithRedirect,
    logout,
  } = useAuth0();



  const toggle = () => setIsOpen(!isOpen);

  const logoutWithRedirect = () =>
    logout({
      returnTo: window.location.origin,
    });
  // let isAdmin = false;
  // const isAdmin = false;
  // if(user){
//   const isAdmin = () => {
//     console.log("in this method");
//     if(user){
//       window.axios.get('/users/' + user.email).then(function (response) {
//         console.log(response);
//         debugger
//         if(response.data.role == "ADMIN"){
//           return true;
//         }else{
//           return false;
//         }
//     }).catch(function (error) {
//           // handle error
//           console.log(error);
//           return false;
//       })
//     }else{
//     return false;
//   } 
// }

const getUserInfo = (user) => {
  if(user){
    let result = false;
    window.axios.get('/users/' + user.email).then(function (response) {
      if(response.data.role == "ADMIN"){
        result =  true;
                  }
    }).catch(function (error) {
                  // handle error
                  console.log(error);
                  result =  false;
              })
              return result;
  }else{
    console.log("in else");
    return false;
  }
  // if(user){
  //         window.axios.get('/users/' + user.email).then(function (response) {
  //           console.log(response);
  //           if(response.data.role == "ADMIN"){
  //             return true;
  //           }else{
  //             return false;
  //           }
  //       }).catch(function (error) {
  //             // handle error
  //             console.log(error);
  //             return false;
  //         })
  //       }else{
  //       return false;
  //     } 
};
const isAdmin = getUserInfo(user);
  // }else{
  //   const isAdmin = false;
  // }
  // const checkAdmin = () =>{
  //   console.log("in admin", isAdmin)
  //   return isAdmin;
  // }
  // getUserInfo(user);
  return (
    <div className="nav-container">
      <Navbar color="light" light expand="md">
        <Container>
          <NavbarBrand className="logo" />
          <NavbarToggler onClick={toggle} />
          <Collapse isOpen={isOpen} navbar>
            <Nav className="mr-auto" navbar>
              <NavItem>
                <NavLink
                  tag={RouterNavLink}
                  to="/"
                  exact
                  // activeClassName="router-link-exact-active"
                >
                  Home 
                </NavLink>
              </NavItem>

              { isAuthenticated && isAdmin && (<NavItem>
                <NavLink
                  tag={RouterNavLink}
                  to={{
                    pathname: "/account",
                    email: user.email
                  }}
                  exact
                  // activeClassName="router-link-exact-active"
                >
                  Account 
                </NavLink>
              </NavItem>

              )}

                {isAuthenticated && isAdmin && (<NavItem>
                <NavLink
                  tag={RouterNavLink}
                  to="/transfer"
                  exact
                  // activeClassName="router-link-exact-active"
                >
                  Fund Transfer
                </NavLink>
              </NavItem>
              )}

                { isAuthenticated && !isAdmin && (<NavItem>
                <NavLink
                  tag={RouterNavLink}
                  to="/refund"
                  exact
                  // activeClassName="router-link-exact-active"
                >
                  Refund
                </NavLink>
              </NavItem>
              )}


              {/* {isAuthenticated && (
                <NavItem>
                  <NavLink
                    tag={RouterNavLink}
                    to="/external-api"
                    exact
                    activeClassName="router-link-exact-active"
                  >
                    External API
                  </NavLink>
                </NavItem>
              )} */}
            </Nav>
            <Nav className="d-none d-md-block" navbar>
              {!isAuthenticated && (
                <NavItem>
                  <Button
                    id="qsLoginBtn"
                    color="primary"
                    className="btn-margin"
                    onClick={() => loginWithRedirect()}
                  >
                    Log in
                  </Button>
                </NavItem>
              )}
              {isAuthenticated && (
                <UncontrolledDropdown nav inNavbar>
                  <DropdownToggle nav caret id="profileDropDown">
                    <img
                      src={user.picture}
                      alt="Profile"
                      className="nav-user-profile rounded-circle"
                      width="50"
                    />
                  </DropdownToggle>
                  <DropdownMenu>
                    <DropdownItem header>{user.name}</DropdownItem>
                    <DropdownItem
                      tag={RouterNavLink}
                      to="/profile"
                      className="dropdown-profile"
                      activeClassName="router-link-exact-active"
                    >
                      <FontAwesomeIcon icon="user" className="mr-3" /> Profile
                    </DropdownItem>
                    <DropdownItem
                      id="qsLogoutBtn"
                      onClick={() => logoutWithRedirect()}
                    >
                      <FontAwesomeIcon icon="power-off" className="mr-3" /> Log
                      out
                    </DropdownItem>
                  </DropdownMenu>
                </UncontrolledDropdown>
              )}
            </Nav>
            {!isAuthenticated && (
              <Nav className="d-md-none" navbar>
                <NavItem>
                  <Button
                    id="qsLoginBtn"
                    color="primary"
                    block
                    onClick={() => loginWithRedirect({})}
                  >
                    Log in
                  </Button>
                </NavItem>
              </Nav>
            )}
            {isAuthenticated && (
              <Nav
                className="d-md-none justify-content-between"
                navbar
                style={{ minHeight: 170 }}
              >
                <NavItem>
                  <span className="user-info">
                    <img
                      src={user.picture}
                      alt="Profile"
                      className="nav-user-profile d-inline-block rounded-circle mr-3"
                      width="50"
                    />
                    <h6 className="d-inline-block">{user.name}</h6>
                  </span>
                </NavItem>
                <NavItem>
                  <FontAwesomeIcon icon="user" className="mr-3" />
                  <RouterNavLink
                    to="/profile"
                    activeClassName="router-link-exact-active"
                  >
                    Profile
                  </RouterNavLink>
                </NavItem>
                <NavItem>
                  <FontAwesomeIcon icon="power-off" className="mr-3" />
                  <RouterNavLink
                    to="#"
                    id="qsLogoutBtn"
                    onClick={() => logoutWithRedirect()}
                  >
                    Log out
                  </RouterNavLink>
                </NavItem>
              </Nav>
            )}
          </Collapse>
        </Container>
      </Navbar>
    </div>
  );
};

export default NavBar;
