import React, { Component } from "react";

import logo from "../assets/logo.svg";
import UserProfileForm from "./UserProfileForm";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import { withAuth0 } from '@auth0/auth0-react';
const axios = require('axios');

// let accountRecords = null;
// let accountRow = [];
class AccountView extends Component {

    constructor(props) {
        super(props);
        console.log(props);
        // const { user, isAuthenticated } = this.props.auth0;
        this.state = { email: props.email, accountRows: false }
        this.getAccounts = this.getAccounts.bind(this);
        // this.state.accountNumber = this.props.accountNumber;
        // this.search = this.search.bind(this);
    }

    componentDidMount() {
        let currentComponent = this;
        this.getAccounts(currentComponent);
    }

    getAccounts(currentComponent) {
        window.axios.get('/users/' + currentComponent.state.email + '/accounts').then(function (response) {
            if (response && response.data) {
                // accountRecords = response.data;
                response.data.forEach(account => {
                    console.log(account);
                    // debugger
                    accountRow.push(<tr>
                        <td>{account.account_number}</td>
                        <td>{account.account_type}</td>
                        <td>{account.balance}</td>
                        <td>{account.date_opened}</td>
                        <td>{account.status}</td>
                    </tr>)
                })
            //     searchTransaction = rowTransactionsRecords;
            debugger
                    console.log(this.state);
                currentComponent.setState({ rowsAccounts: accountRow })
            } else {
                currentComponent.setState({ rowsAccounts: null });
            }
        }).catch(function (error) {
            // handle error
            console.log(error);
            accountRecords = null;
            accountRow = null;
        })
    }

    render() {
        return (
            <div>
                { this.state.accountRows ? 
                <table class="table">
                    <thead>
                        <tr>
                            <th><abbr title="number">Account Number</abbr></th>
                            <th><abbr title="type"></abbr>Account Type</th>
                            <th><abbr title="balance">Balance</abbr></th>
                            <th><abbr title="date">Opening Date</abbr></th>
                            <th><abbr title="status">Status</abbr></th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.accountRows}
                    </tbody>
                </table>
                :
                <div>

                </div>
            }
            <Link
          to={{
            pathname: "/createAccount",
            email: this.state.email
          }}> Create Account {this.state.email}</Link>
            </div>
        )
    };
}

export default withAuth0(AccountView);
