import React, { Component } from "react";

import { Row, Col } from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const axios = require('axios');


class UserProfileForm extends Component {
    constructor(props) {
        super(props);

        this.users = {'first_name': 'Riddhi Jain'};
        this.createUser = this.createUser.bind(this);
        this.getUser = this.getUser.bind(this);
        this.getUser();
      }
        componentDidMount() {
            window.addEventListener('load', this.getUser);
        }

        getUser(){
            console.log("Calling get user");
            axios.get('http://localhost:8080/users/riddhi.jain@sjsu.edu').then(function (response) {
                // handle success
                console.log(response);
                this.user = response
              })
              .catch(function (error) {
                // handle error
                console.log(error);
              })
        }

        createUser() {
            const requestOptions = {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    "firstName": "Riddhi2",
                    "lastName": "Jain",
                    "email": "riddhi.jain+1@sjsu.edu",
                    "identity_no": "abcdefghi+1",
                    "dob": null,
                    "address": null,
                    "state": "california",
                    "zipCode": null,
                    "mobileNumber": null
                })
            };

            axios.post('http://localhost:8080/users', {
                "firstName": "Riddhi2",
                "lastName": "Jain",
                "email": "riddhi.jain+1@sjsu.edu",
                "identity_no": "abcdefghi+1",
                "dob": null,
                "address": null,
                "state": "california",
                "zipCode": null,
                "mobileNumber": null
            }).then(function (response) {
                console.log(response);
              })
              .catch(function (error) {
                console.log(error);
              });

            fetch('http://localhost:8080/users', requestOptions)
                .then(response => response.json())
                .then(data => this.setState({ users: data })
                .catch(console.log));
        }
  render() {
    return (
        <div class="container">
        <div class="columns">
        <div class="column">
            <label class="label">First Name: {this.users.first_name}</label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" />
            </div>
        </div>
        <div class="column">
            <label class="label">Last Name</label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" />
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Email Id</label>
            <div class="control has-icons-left has-icons-right">
                <input class="input is-danger" type="email" placeholder="Email input" />
                <span class="icon is-small is-left">
                    <i class="fas fa-envelope"></i>
                </span>
                <span class="icon is-small is-right">
                    <i class="fas fa-exclamation-triangle"></i>
                </span>
            </div>
            <p class="help is-danger">This email is invalid</p>
        </div>

        <div class="column">
            <label class="label">Mobile No</label>
            <div class="control">
                <input class="input" type="tel" placeholder="Number input" />
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Identity No</label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" />
            </div>
        </div>

        <div class="column">
            <label class="label">Date Of Birth</label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" />
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Address</label>
            <textarea class="textarea" placeholder="2 lines of textarea" rows="2"></textarea>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">State</label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" />
            </div>
        </div>

        <div class="column">
            <label class="label">Zip Code</label>
            <div class="control">
                <input class="input" type="text" placeholder="Text input" />
            </div>
        </div>
        </div>
        <div class="center">
        <div class="control">
            <button class="button is-primary" onClick={this.createUser}>Submit</button>
        </div>
        </div>
        </div>
    );
  }
}

export default UserProfileForm;
