import React, { Component } from "react";

import { Row, Col } from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const axios = require('axios');

let user = false;
class UserProfileForm extends Component {
    constructor(props) {
        super(props);
        this.state = {user: {}, email: props.email}
        this.createUser = this.createUser.bind(this);
        this.getUser = this.getUser.bind(this);
        this.updateUser = this.updateUser.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
      }
        componentDidMount() {
            let currentComponent = this;
            this.getUser(currentComponent);
        }

        handleInputChange(event) {
            console.log(event.target.value);
            console.log(event.target.name);
            // this.setState({user: event.target.value});
        }


        getUser(currentComponent){
            axios.get('http://localhost:8080/users/'+currentComponent.state.email).then(function (response) {
                // handle success
                currentComponent.setState({user: response.data});
              })
              .catch(function (error) {
                // handle error
                console.log(error);
              })
            }

        createUser() {
            axios.post('http://localhost:8080/users', {
                "firstName": "Riddhi3",
                "lastName": "Jain",
                "email": "riddhi.jain+2@sjsu.edu",
                "identity_no": "abcdefghi+1",
                "dob": null,
                "address": null,
                "state": "california",
                "zipCode": null,
                "mobileNumber": null
                }).then(function (response) {
                this.user = response.data;
              })
              .catch(function (error) {
                console.log(error);
              });
        }

        updateUser(){
            console.log(this.state.user);
            axios.put('http://localhost:8080/users/'+this.state.user.userId, {
                "firstName": this.state.user.firstName,
                "lastName": this.state.user.lastName,
                "email": this.state.user.email,
                "identity_no": this.state.user.identity_no,
                "dob": this.state.user.dob,
                "address": this.state.user.address,
                "state": this.state.user.state,
                "zipCode": this.state.user.zipCode,
                "mobileNumber": this.state.user.mobileNumber
                }).then(function (response) {
                    console.log(response);
                // this.user = response.data;
              })
              .catch(function (error) {
                console.log(error);
              });
        }
  render() {
    return (
        <div class="container">
        <div class="columns">
        <div class="column">
            <label class="label">First Name: {this.state.user.firstName}</label>
            <div class="control">
                {/* <input class="input" type="text" placeholder="Text input" value={this.state.user.firstName} disabled/> */}
                <input name="firstName" class="input" type="text" placeholder="Text input" value={this.state.user.firstName} onChange={this.handleInputChange}/>
            </div>
        </div>
        <div class="column">
            <label class="label">Last Name: </label>
            <div class="control">
                <input name="lastName" class="input" type="text" placeholder="Text input" value={this.state.user.lastName} onChange={this.handleInputChange}/>
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Email Id:</label>
            <div class="control has-icons-left has-icons-right">
                <input name="email" class="input is-danger" type="email" placeholder="Email input"  value={this.state.user.email} disabled/>
                <span class="icon is-small is-left">
                    <i class="fas fa-envelope"></i>
                </span>
                <span class="icon is-small is-right">
                    <i class="fas fa-exclamation-triangle"></i>
                </span>
            </div>
            <p class="help is-danger">This email is invalid</p>
        </div>

        <div class="column">
            <label class="label">Mobile No: </label>
            <div class="control">
                <input name="mobileNumber" class="input" type="tel" placeholder="Number input" value ={this.state.user.mobileNumber} disabled/>
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Identity No: </label>
            <div class="control">
                <input name="identity_no" class="input" type="text" placeholder="Text input" value={this.state.user.identity_no} disabled/>
            </div>
        </div>

        <div class="column">
            <label class="label">Date Of Birth: </label>
            <div class="control">
                <input name="dob" class="input" type="text" placeholder="Text input" value={this.state.user.dob} disabled/>
            </div>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">Address: </label>
            <textarea name="address" class="textarea" placeholder="2 lines of textarea" rows="2" value={this.state.user.address} disabled></textarea>
        </div>
        </div>
        <div class="columns">
        <div class="column">
            <label class="label">State: </label>
            <div class="control">
                <input name="state" class="input" type="text" placeholder="Text input" value={this.state.user.state} disabled/>
            </div>
        </div>

        <div class="column">
            <label class="label">Zip Code: </label>
            <div class="control">
                <input name="zip_code" class="input" type="text" placeholder="Text input" value={this.state.user.zipCode} disabled/>
            </div>
        </div>
        </div>
        <div class="center">
        <div class="control">
            <button class="button is-primary" onClick={this.updateUser}>Update</button>
        </div>
        </div>
        </div>


        // ------------------

        // <div class="container">
        // <div class="columns">
        // <div class="column">
        //     <label class="label">First Name: </label>
        //     <div>{this.state.user.firstName}</div>
        // </div>
        // <div class="column">
        //     <label class="label">Last Name: </label>
        //     <div>{this.state.user.lastName}</div>
        // </div>
        // </div>
        // <div class="columns">
        // <div class="column">
        //     <label class="label">Email Id: </label>
        //     <div>{this.state.user.email}</div>
        // </div>

        // <div class="column">
        //     <label class="label">Mobile No: </label>
        //     <div>{this.state.user.mobileNumber}</div>
        // </div>
        // </div>
        // <div class="columns">
        // <div class="column">
        //     <label class="label">Identity No: 
        //     <div>{this.state.user.identity_no}</div></label>
        // </div>

        // <div class="column">
        //     <label class="label">Date Of Birth: </label>
        //     <div>{this.state.user.dob}</div>
        // </div>
        // </div>
        // <div class="columns">
        // <div class="column">
        //     <label class="label">Address: </label>
        //     <div>{this.state.user.address}</div>
        // </div>
        // </div>
        // <div class="columns">
        // <div class="column">
        //     <label class="label">State: </label>
        //     <div>{this.state.user.state}</div>
        // </div>

        // <div class="column">
        //     <label class="label">Zip Code:</label>
        //     <div>{this.state.user.zipCode}</div>
        // </div>
        // </div>
        // <div class="center">
        // <div class="control">
        //     <button class="button is-primary" onClick={this.editUser}>Edit</button>
        // </div>
        // </div>
        // </div>
    );
  }
}

export default UserProfileForm;
